import React, { useEffect, useState } from "react";
import {Link} from "react-router-dom";
import "../css/CropDistribution.css";
import Chart from "chart.js/auto";

export default function Area(props) {
  let [cropdata, setCropdata] = useState([]);
  let [cropnames,setCropnames] = useState([]);
  let [cropArea,setCropArea] = useState([]);

  const fetchCropData = async () => {
    try {
      const response = await fetch("http://localhost:5000/getArea", {
        method: "post",
        body: JSON.stringify({}),
        headers: {
          "Content-Type": "application/json",
        },
      });
      const data = await response.json();
      setCropdata(data.data);
      ids = data.data.map(item=>item._id);
      area = data.data.map(item=>item.totalArea);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  const createChart = () => {
    const ctx = document.getElementById("myChartArea");
    new Chart(ctx, {
      type: "bar",
      data: {
        labels: cropnames,
        datasets: [
          {
            label: "Crop area",
            data: cropArea,
            borderWidth: 1,
          },
        ],
      },
      options: {
        scales: {
          y: {
            beginAtZero: true,
          },
        },
      },
    });
  };

  useEffect(() => {
    fetchCropData();
  }, []);



  useEffect(() => {
    if (cropdata.length > 0) {
      let ids = cropdata.map(item=>item._id);
      let area = cropdata.map(item=>item.totalArea);
      useEffect(()=>{
        if(cropnames.length>0 && cropArea.length>0){
          setCropnames(ids)
          setCropArea(area)
          createChart();
        }
      },[cropArea,cropnames])
    }
  }, [cropdata]);

  return (
    <>
    <nav className="navCropDistribution">
        <div className="containerCropDistribution">
         <h1>Area</h1>
        </div>
        {/* <Link to={`/${backurl}`}>Back</Link> */}
    </nav>
      <div className="crop_sowingdata_bar">
        <div className="prevyeargraph">
          <h2
            style={{
              fontFamily: "sans-serif",
              fontWeight: 600,
              color: "blueviolet",
            }}
          >
           Area Sowing Distribution
          </h2>
          <div style={{ width: 800, height: 400 }}>
            <canvas id="myChartArea"></canvas>
          </div>
        </div>
      </div>
    </>
  );
}
