import React from "react";
import {Link, Route, BrowserRouter as Router, Navigate, Routes} from "react-router-dom"; //this used for routing
import Cover  from "./components/Cover";
import SubmitCrop from "./components/SubmitCrop";
import Home from "./components/Home";
import Expert from "./components/Expert";
import PesticideGuide from "./components/PesticideGuide";
import F2F_Home from "./components/F2F_Home";
import NewPost from "./components/NewPost";
import Editpost from "./components/Editpost";
import SowingGuide from "./components/SowingGuide";
import StateCropStatistics from "./components/StateCropStatistics";

const mahacrops = {Jowar:require("./Assets/images/jowarlogo.webp"),Wheat:require("./Assets/images/wheatlogo.avif"),Bajara:require("./Assets/images/bajralogo.jpg"),Sugarcane:require("./Assets/images/sugarcanelogo.jpg"),Onion:require("./Assets/images/onionlogo.jpg")};


export default function App(){
    return(
        <>
        <Router>
            <Routes>
                <Route
                exact
                path="/"
                element={<Cover />}
                />
                <Route
                exact
                path="/home"
                element={<Home />}
                />
                <Route
                exact
                path="/submitcrop"
                element={<SubmitCrop />}
                />
                <Route
                exact
                path="/pesticideguide" //this are links/paths of front end & PesticideGuide.jsx contains link/path of backend server
                element={<PesticideGuide />} //this means through this <PesticideGuide />, we are connecting to backend
                />
                <Route
                exact
                path="/expert"
                element={<Expert />}
                />
                <Route
                exact
                path="/farmertofarmer"
                element={<F2F_Home />}
                />
                <Route
                exact
                path="/sowingguide"
                element={<SowingGuide />}
                />

                {/* State wise route */}
                <Route
                exact
                path="/maharashtra"
                element={<StateCropStatistics state={"Maharashtra"} crops={mahacrops} />}
                />
                <Route
                exact
                path="/madhypradesh"
                element={<StateCropStatistics state={"Madhypradesh"} crops={mahacrops} />}
                />
                <Route
                exact
                path="/uttarpradesh"
                element={<StateCropStatistics state={"Uttarpradesh"} crops={mahacrops} />}
                />
                <Route
                exact
                path="/punjab"
                element={<StateCropStatistics state={"Punjab"} crops={mahacrops} />}
                />
                <Route
                exact
                path="/westbengal"
                element={<StateCropStatistics state={"West Bengal"} crops={mahacrops} />}
                />

                {/*  */}
                <Route
                exact
                path="/newpost"
                element={<NewPost />}
                />
                 <Route
                exact
                path="/editpost"
                element={<Editpost />}
                />
            </Routes>
        </Router>
        </>
    );
}
