import React, { useEffect, useState } from "react";
import {Link} from "react-router-dom";
import "../css/CropDistribution.css";
import Chart from "chart.js/auto";

export default function Area(props) {
  let [cropdata, setCropdata] = useState([]);
  var chart;
  const fetchCropData = async () => {
    try {
      const response = await fetch("http://localhost:5000/getArea", {
        method: "post",
        body: JSON.stringify({}),
        headers: {
          "Content-Type": "application/json",
        },
      });
      const data = await response.json();
      setCropdata(data.data);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  const createChart = () => {
    const ctx = document.getElementById("myChartArea");
    chart = new Chart(ctx, {
      type: "bar",
      data: {
        labels: cropdata.map(item=>item._id),
        datasets: [
          {
            label: "Crop Area",
            data: cropdata.map(item=>item.totalArea),
            borderWidth: 1,
          },
        ],
      },
      options: {
        scales: {
          y: {
            beginAtZero: true,
          },
        },
      },
    });
  };

  useEffect(() => {
    fetchCropData();
  }, []);


  useEffect(() => {
    if (cropdata.length > 0) {
        if(chart){
          chart.dis
        }
          createChart();
        }
  }, [cropdata]);

  return (
    <>
    <nav className="navCropDistribution">
        <div className="containerCropDistribution">
         <h1>Area</h1>
        </div>
        {/* <Link to={`/${backurl}`}>Back</Link> */}
    </nav>
      <div className="crop_sowingdata_bar">
        <div className="prevyeargraph">
          <h2
            style={{
              fontFamily: "sans-serif",
              fontWeight: 600,
              color: "blueviolet",
            }}
          >
           Area Sowing Distribution
          </h2>
          <div style={{ width: 800, height: 400 }}>
            <canvas id="myChartArea"></canvas>
          </div>
        </div>
      </div>
    </>
  );
}
